const State = {
    loading: 0,
    loaded: 1,
    error: 2
}

export class ImageManager {
    constructor(options) {
        this.el = options.el
        this.src = options.src
        this.state =0
        this.loading = options.loading
        this.error = options.error
        this.render(this.src)
    }
    render(src) {
        this.el.setAttribute('src', src)
    }
    load(next) {
        if (this.state>0) {
            return
        }
        this.renderSrc(next)
    }
    renderSrc(next) {
            this.state = 1
            this.render(this.src)         
    }
}
